    const check = () => {
      const currentSize = JSON.stringify([document.documentElement.scrollWidth, document.documentElement.scrollHeight]);
      if (currentSize === lastSize) {
        resolve();
      } else {
        lastSize = currentSize;
        requestAnimationFrame(check);
      }
    };
    requestAnimationFrame(check);
  });
});

