async function loginWithCredentials(page: Page, username: string, password: string, options?: { rememberMe?: boolean; timeout?: number }) {
  const timeout = options?.timeout ?? 30000;
  await page.goto('/login', { waitUntil: 'domcontentloaded' });
  await page.getByLabel(/username|email/i).fill(username);
  await page.getByLabel(/password/i).fill(password);
  if (options?.rememberMe) {
    await page.getByLabel(/remember/i).check();
  }
  await page.getByRole('button', { name: /login|sign\s*in/i }).click();
  await page.waitForURL(/dashboard|home/i, { timeout });
  await expect(page.locator('[data-testid="user-menu"]')).toBeVisible({ timeout: 5000 });
}

async function loginWithOAuth(page: Page, provider: 'google' | 'github' | 'microsoft', credentials: { email: string; password: string }) {
  await page.goto('/login');
  await page.getByRole('button', { name: new RegExp(provider, 'i') }).click();
  const popupPromise = page.waitForEvent('popup');
  const popup = await popupPromise;
  await popup.waitForLoadState('domcontentloaded');
  await popup.getByLabel(/email|username/i).fill(credentials.email);
  await popup.getByLabel(/password/i).fill(credentials.password);
  await popup.getByRole('button', { name: /sign\s*in|login|continue/i }).click();
  await popup.waitForEvent('close');
  await page.waitForURL(/dashboard|home/i, { timeout: 30000 });
}

async function loginWithSSO(page: Page, domain: string, credentials: { email: string; password: string }) {
  await page.goto('/login');
  await page.getByRole('link', { name: /sso|enterprise\s*login/i }).click();
  await page.getByLabel(/domain|organization/i).fill(domain);
  await page.getByRole('button', { name: /continue|next/i }).click();
  await page.waitForNavigation({ waitUntil: 'domcontentloaded' });
  await page.getByLabel(/email|username/i).fill(credentials.email);
  await page.getByLabel(/password/i).fill(credentials.password);
  await page.getByRole('button', { name: /sign\s*in/i }).click();
  await page.waitForURL(/dashboard|home/i, { timeout: 30000 });
}

async function loginWithMFA(page: Page, username: string, password: string, totpSecret: string) {
  const otplib = require('otplib');
  await page.goto('/login');
  await page.getByLabel(/username|email/i).fill(username);
  await page.getByLabel(/password/i).fill(password);
  await page.getByRole('button', { name: /login|sign\s*in/i }).click();
  await page.waitForSelector('[data-testid="mfa-code-input"]', { timeout: 10000 });
  const token = otplib.authenticator.generate(totpSecret);
  await page.getByLabel(/code|token|otp/i).fill(token);
  await page.getByRole('button', { name: /verify|continue|submit/i }).click();
  await page.waitForURL(/dashboard|home/i, { timeout: 30000 });
}

async function logout(page: Page, options?: { skipConfirmation?: boolean }) {
  await page.getByRole('button', { name: /user\s*menu|profile|account/i }).click();
  await page.getByRole('menuitem', { name: /logout|sign\s*out/i }).click();
  if (!options?.skipConfirmation) {
    const confirmButton = page.getByRole('button', { name: /confirm|yes|logout/i });
    if (await confirmButton.isVisible()) {
      await confirmButton.click();
    }
  }
  await page.waitForURL(/login|signin|auth/i, { timeout: 10000 });
  await expect(page.getByRole('heading', { name: /login|sign\s*in/i })).toBeVisible();
}

async function register(page: Page, userData: { email: string; password: string; firstName: string; lastName: string }) {
  await page.goto('/register');
  await page.getByLabel(/first\s*name/i).fill(userData.firstName);
  await page.getByLabel(/last\s*name/i).fill(userData.lastName);
  await page.getByLabel(/email/i).fill(userData.email);
  await page.getByLabel(/^password$/i).fill(userData.password);
  await page.getByLabel(/confirm\s*password/i).fill(userData.password);
  await page.getByRole('checkbox', { name: /terms|agreement/i }).check();
  await page.getByRole('button', { name: /sign\s*up|register|create/i }).click();
  await expect(page.getByText(/success|verification\s*email|check\s*your\s*email/i)).toBeVisible({ timeout: 10000 });
}

async function forgotPassword(page: Page, email: string) {
  await page.goto('/login');
  await page.getByRole('link', { name: /forgot\s*password/i }).click();
  await page.waitForURL(/forgot|reset/i);
  await page.getByLabel(/email/i).fill(email);
  await page.getByRole('button', { name: /send|submit|reset/i }).click();
  await expect(page.getByText(/email\s*sent|check\s*your\s*email/i)).toBeVisible({ timeout: 10000 });
}

async function resetPassword(page: Page, resetToken: string, newPassword: string) {
  await page.goto(`/reset-password?token=${resetToken}`);
  await page.waitForSelector('[data-testid="new-password-input"]');
  await page.getByLabel(/new\s*password/i).fill(newPassword);
  await page.getByLabel(/confirm\s*password/i).fill(newPassword);
  await page.getByRole('button', { name: /reset|update|change/i }).click();
  await expect(page.getByText(/success|updated|changed/i)).toBeVisible({ timeout: 10000 });
  await page.waitForURL(/login/i, { timeout: 5000 });
}

async function changePassword(page: Page, currentPassword: string, newPassword: string) {
  await page.goto('/settings/security');
  await page.getByLabel(/current\s*password/i).fill(currentPassword);
  await page.getByLabel(/new\s*password/i).fill(newPassword);
  await page.getByLabel(/confirm\s*password/i).fill(newPassword);
  await page.getByRole('button', { name: /change|update|save/i }).click();
  await expect(page.getByText(/success|updated|changed/i)).toBeVisible({ timeout: 10000 });
}

async function verifyEmail(page: Page, verificationToken: string) {
  await page.goto(`/verify-email?token=${verificationToken}`);
  await page.waitForLoadState('domcontentloaded');
  const verifyButton = page.getByRole('button', { name: /verify|confirm/i });
  if (await verifyButton.isVisible()) {
    await verifyButton.click();
  }
  await expect(page.getByText(/verified|success|confirmed/i)).toBeVisible({ timeout: 10000 });
}

async function refreshSession(page: Page) {
  const response = await page.evaluate(async () => {
    const res = await fetch('/api/auth/refresh', {
      method: 'POST',
      credentials: 'include'
    });
    return res.ok;
  });
  if (!response) {
    throw new Error('Session refresh failed');
  }
  await page.reload({ waitUntil: 'domcontentloaded' });
  await expect(page.locator('[data-testid="user-menu"]')).toBeVisible({ timeout: 5000 });
}

async function rememberMe(page: Page, username: string, password: string) {
  await page.goto('/login');
  await page.getByLabel(/username|email/i).fill(username);
  await page.getByLabel(/password/i).fill(password);
  await page.getByRole('checkbox', { name: /remember/i }).check();
  await page.getByRole('button', { name: /login|sign\s*in/i }).click();
  await page.waitForURL(/dashboard|home/i);
  await page.context().storageState({ path: '.auth-states/remembered.json' });
}

async function accountLock(page: Page, username: string, maxAttempts: number = 3) {
  await page.goto('/login');
  for (let i = 0; i < maxAttempts; i++) {
    await page.getByLabel(/username|email/i).fill(username);
    await page.getByLabel(/password/i).fill('wrong-password');
    await page.getByRole('button', { name: /login|sign\s*in/i }).click();
    await page.waitForTimeout(1000);
  }
  await expect(page.getByText(/locked|blocked|suspended/i)).toBeVisible({ timeout: 10000 });
}

async function passwordStrength(page: Page, password: string, expectedStrength: 'weak' | 'medium' | 'strong') {
  await page.goto('/register');
  const passwordInput = page.getByLabel(/^password$/i);
  await passwordInput.fill(password);
  const strengthIndicator = page.locator('[data-testid="password-strength"]');
  await expect(strengthIndicator).toHaveText(new RegExp(expectedStrength, 'i'));
  const strengthClass = await strengthIndicator.getAttribute('class');
  expect(strengthClass).toContain(expectedStrength);
}

async function biometricAuth(page: Page) {
  await page.goto('/login');
  await page.getByRole('button', { name: /biometric|fingerprint|face\s*id/i }).click();
  await page.evaluate(() => {
    window.dispatchEvent(new CustomEvent('biometric-success'));
  });
  await page.waitForURL(/dashboard|home/i, { timeout: 30000 });
  await expect(page.locator('[data-testid="user-menu"]')).toBeVisible();
}

async function navigateViaSidebar(page: Page, menuItem: string, options?: { expandParent?: string }) {
  const sidebar = page.locator('[data-testid="sidebar"]');
  if (options?.expandParent) {
    const parent = sidebar.getByRole('button', { name: new RegExp(options.expandParent, 'i') });
    if (await parent.getAttribute('aria-expanded') === 'false') {
      await parent.click();
    }
  }
  await sidebar.getByRole('link', { name: new RegExp(menuItem, 'i') }).click();
  await page.waitForLoadState('domcontentloaded');
  await expect(page).toHaveURL(new RegExp(menuItem.toLowerCase().replace(/\s+/g, '-')));
}

async function navigateViaBreadcrumb(page: Page, breadcrumbText: string) {
  const breadcrumb = page.locator('[data-testid="breadcrumb"]').getByRole('link', { name: new RegExp(breadcrumbText, 'i') });
  await expect(breadcrumb).toBeVisible();
  await breadcrumb.click();
  await page.waitForLoadState('domcontentloaded');
  await expect(page.getByRole('heading', { name: new RegExp(breadcrumbText, 'i') })).toBeVisible();
}

async function navigateViaTabs(page: Page, tabName: string) {
  const tab = page.getByRole('tab', { name: new RegExp(tabName, 'i') });
  await expect(tab).toBeVisible();
  await tab.click();
  await expect(tab).toHaveAttribute('aria-selected', 'true');
  const tabPanel = page.locator(`[aria-labelledby="${await tab.getAttribute('id')}"]`);
  await expect(tabPanel).toBeVisible();
}

async function usePagination(page: Page, pageNumber: number) {
  const pagination = page.locator('[data-testid="pagination"]');
  await pagination.getByRole('button', { name: String(pageNumber) }).click();
  await page.waitForLoadState('networkidle');
  await expect(pagination.getByRole('button', { name: String(pageNumber) })).toHaveAttribute('aria-current', 'page');
  await expect(page.locator('[data-testid="page-content"]')).toBeVisible();
}

async function useInfinitePagination(page: Page, scrollCount: number = 3) {
  const container = page.locator('[data-testid="scrollable-content"]');
  for (let i = 0; i < scrollCount; i++) {
    await container.evaluate(el => el.scrollTop = el.scrollHeight);
    await page.waitForTimeout(1000);
    await page.waitForLoadState('networkidle');
  }
  const items = await page.locator('[data-testid="list-item"]').count();
  expect(items).toBeGreaterThan(scrollCount * 10);
}

async function completeWizardStep(page: Page, stepNumber: number, fillData?: Record<string, string>) {
  const wizard = page.locator('[data-testid="wizard"]');
  const currentStep = wizard.locator(`[data-step="${stepNumber}"]`);
  await expect(currentStep).toBeVisible();
  if (fillData) {
    for (const [label, value] of Object.entries(fillData)) {
      await currentStep.getByLabel(new RegExp(label, 'i')).fill(value);
    }
  }
  await currentStep.getByRole('button', { name: /next|continue|proceed/i }).click();
  await expect(wizard.locator(`[data-step="${stepNumber + 1}"]`)).toBeVisible();
}

async function expandTreeNode(page: Page, nodeName: string) {
  const node = page.getByRole('treeitem', { name: new RegExp(nodeName, 'i') });
  await expect(node).toBeVisible();
  if (await node.getAttribute('aria-expanded') === 'false') {
    await node.getByRole('button', { name: /expand|toggle/i }).click();
    await expect(node).toHaveAttribute('aria-expanded', 'true');
  }
  await page.waitForTimeout(300);
}

async function useCommandPalette(page: Page, command: string) {
  await page.keyboard.press('Meta+K');
  const palette = page.locator('[data-testid="command-palette"]');
  await expect(palette).toBeVisible();
  await palette.getByRole('textbox').fill(command);
  await page.waitForTimeout(300);
  await palette.getByRole('option', { name: new RegExp(command, 'i') }).first().click();
  await expect(palette).not.toBeVisible();
}

async function useMegaMenu(page: Page, mainMenu: string, subMenuItem: string) {
  const menuTrigger = page.getByRole('button', { name: new RegExp(mainMenu, 'i') });
  await menuTrigger.hover();
  const megaMenu = page.locator('[data-testid="mega-menu"]');
  await expect(megaMenu).toBeVisible();
  await megaMenu.getByRole('link', { name: new RegExp(subMenuItem, 'i') }).click();
  await page.waitForLoadState('domcontentloaded');
}

async function useContextMenu(page: Page, targetSelector: string, menuItem: string) {
  await page.locator(targetSelector).click({ button: 'right' });
  const contextMenu = page.locator('[role="menu"]');
  await expect(contextMenu).toBeVisible();
  await contextMenu.getByRole('menuitem', { name: new RegExp(menuItem, 'i') }).click();
  await expect(contextMenu).not.toBeVisible();
}

async function navigateToUrl(page: Page, url: string, options?: { waitUntil?: 'load' | 'domcontentloaded' | 'networkidle' }) {
  const waitUntil = options?.waitUntil ?? 'domcontentloaded';
  await page.goto(url, { waitUntil });
  await expect(page).toHaveURL(new RegExp(url.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')));
  await page.waitForLoadState('domcontentloaded');
}

async function waitForNavigation(page: Page, options?: { timeout?: number; url?: string | RegExp }) {
  const timeout = options?.timeout ?? 30000;
  if (options?.url) {
    await page.waitForURL(options.url, { timeout });
  } else {
    await page.waitForLoadState('networkidle', { timeout });
  }
  await expect(page.locator('body')).toBeVisible();
}

async function sortTableColumn(page: Page, columnName: string, direction: 'asc' | 'desc' = 'asc') {
  const header = page.getByRole('columnheader', { name: new RegExp(columnName, 'i') });
  await expect(header).toBeVisible();
  const currentSort = await header.getAttribute('aria-sort');
  if ((direction === 'asc' && currentSort !== 'ascending') || (direction === 'desc' && currentSort !== 'descending')) {
    await header.click();
  }
  if ((direction === 'desc' && currentSort === 'ascending') || (direction === 'asc' && currentSort === 'descending')) {
    await header.click();
  }
  await page.waitForTimeout(500);
  await expect(header).toHaveAttribute('aria-sort', direction === 'asc' ? 'ascending' : 'descending');
}

async function filterTable(page: Page, filters: Record<string, string>) {
  const filterPanel = page.locator('[data-testid="filter-panel"]');
  const filterButton = page.getByRole('button', { name: /filter/i });
  if (!await filterPanel.isVisible()) {
    await filterButton.click();
  }
  for (const [field, value] of Object.entries(filters)) {
    await filterPanel.getByLabel(new RegExp(field, 'i')).fill(value);
  }
  await filterPanel.getByRole('button', { name: /apply|filter|search/i }).click();
  await page.waitForLoadState('networkidle');
}

async function searchTable(page: Page, searchTerm: string) {
  const searchInput = page.locator('[data-testid="table-search"]');
  await searchInput.fill(searchTerm);
  await page.keyboard.press('Enter');
  await page.waitForLoadState('networkidle');
  const rows = page.getByRole('row');
  const visibleRows = await rows.count();
  expect(visibleRows).toBeGreaterThan(0);
  const firstRow = await rows.first().textContent();
  expect(firstRow?.toLowerCase()).toContain(searchTerm.toLowerCase());
}

async function selectTableRow(page: Page, rowIdentifier: string | number, options?: { selectMultiple?: boolean }) {
  const table = page.locator('[role="grid"], table');
  const row = typeof rowIdentifier === 'number' 
    ? table.getByRole('row').nth(rowIdentifier)
    : table.getByRole('row', { name: new RegExp(rowIdentifier, 'i') });
  await expect(row).toBeVisible();
  const checkbox = row.getByRole('checkbox');
  if (await checkbox.isVisible()) {
    await checkbox.check();
  } else {
    await row.click({ modifiers: options?.selectMultiple ? ['Shift'] : [] });
  }
  await expect(row).toHaveAttribute('aria-selected', 'true');
}

async function expandTableRow(page: Page, rowIdentifier: string | number) {
  const row = typeof rowIdentifier === 'number'
    ? page.getByRole('row').nth(rowIdentifier)
    : page.getByRole('row', { name: new RegExp(rowIdentifier, 'i') });
  const expandButton = row.getByRole('button', { name: /expand|show\s*details/i });
